import{_ as a}from"./index.vue.72819e7c.js";import{_ as n}from"./Navbar.vue.06fc2e6e.js";import{u as l}from"./posts.17d75be4.js";import{f as k,c as e,b as o,F as u,r as c,i as g,o as t}from"./entry.df33e24a.js";import"./nuxt-link.b2baa0bc.js";const i=[{_type:"post",id:527,uid:"i30IHN_d",title:"Незаконная выдача квартир вне очереди в Талдыкоргане",slug:"nezakonnaia_vydaca_kvartir_vne_oceredi_v_taldykorgane_527",short:"Госслужащие, журналисты, артисты, спортсмены и представители правоохранительных органов нарушили незаконно получили жильё вне очереди сообщил Бейбит Исабаев.",likes_count:0,me_liked:!1,published_at:"2023-06-13T11:11:04.000000Z",image_preview:"https://kapster.kz/syc/kapster/22869/conversions/64884ec84ab5e-preview.jpg",content:'<p>Квартиры в Талдыкоргане предназначались для очередников. Аким Жетысуской области Бейбит Исабаев предоставил подробности дела о нарушении выдачи квартир в Талдыкоргане. На возобновление очередности чиновники потратили год. </p><blockquote><p><em>"В силу разных обстоятельств не берусь судить руководителей с 2010 года. По городу Талдыкорган образовалась очень большая очередь на получение жилья – порядка 15 тысяч человек. За последние 3-5 лет не был выделен ни один земельный участок под ИЖС. Почему не знаю, все регионы перешли на новый порядок электронной базы с 2016-2017 годов – </em><em>kezekte.kz</em><em>, а именно в нашем регионе, в том числе Талдыкоргане, до начала 2022-го. Очередность была по 19 категориям, а электронные базы ведутся только по шести категориям. Порядок очередности нарушен, списки опустились на десятки и десятки ниже, у горожан много вопросов", – дополнил Исабаев.</em></p></blockquote><p>Данным делом занимаются стражи порядка, после чего все виновные лица понесут наказание, об этом сообщил глава региона.</p><blockquote><p><em>"Самое обидное то, что среди нарушителей были госслужащие, журналисты, представители правоохранительных органов, лица, относящиеся к категории СУСН, артисты, спортсмены. С такими нарушителями мы уже боремся год. Где-то 99 квартир – вопросом занимается Антикор. Фамилии не буду озвучивать, среди них есть известные фамилии. Чуть-чуть осталось, подождём. Мы ввели электронную базу данных. Почему-то были уничтожены все бумажные и электронные варианты очередности с 2003-го по 2022 год", – поделился аким.</em></p></blockquote><p>В марте этого года стало известно, что количество незаконно приобретенных квартир в Талдыкоргане, составило порядком 1200. </p><p>Сейчас под следствием находятся некоторые должностные лица отдела ЖКХ.</p>'},{_type:"post",id:526,uid:"Ru1M-qHy",title:"Планируется повышение налогов для владельцев нескольких квартир в Казахстане",slug:"planiruetsia_povysenie_nalogov_dlia_vladelcev_neskolkix_kvartir_v_kazaxstane_526",short:"Вице-министр национальной экономики РК Азамат Амрин раскрывает детали внедрения новых методов расчета налога на роскошь в Казахстане.",likes_count:0,me_liked:!1,published_at:"2023-06-13T06:15:54.000000Z",image_preview:"https://kapster.kz/syc/kapster/22840/conversions/6488099a42a57-preview.jpg",content:"<p>- Сейчас мы работаем над повышением налога на роскошь. Это было одно из поручений президента. Речь здесь не идет о каком-то отдельном налоге. Это означает, что на отдельную категорию товаров начисляется дополнительный налог. Взимается акцизами, налогом на имущество. Теперь у нас есть и без того прогрессивная шкала налога на имущество физических лиц. Чем выше стоимость имущества, тем выше ставка налога – с 0,01% до 1,5%, – сказал Азамат Амрин, выступая на круглом столе на тему: «Актуальные вопросы налоговой политики и совершенствования налогового администрирования» в сенате.</p><p><br>Он так же разъясняет последствия новой шкалы налогообложения для лиц, обладающих несколькими объектами недвижимости.</p><p><br>- Например, есть владельцы, у которых 10 одинаковых квартир. Налоговая ставка рассчитывается индивидуально для каждой квартиры, которой они владеют. Если стоимость 1-комнатной квартиры составляет 20 млн тенге, ставка налога будет другой. Если сложить стоимость 10 квартир, то получится 200 млн тенге. Ставка налога на 200 млн тенге также должна быть высокой. Поэтому для таких категорий налог на имущество должен исчисляться в зависимости от совокупной стоимости. Ставка налога будет выше, – пояснил вице-министр.</p><p><br>Помимо этого, Азамат Амрин предлагает увеличить транспортный налог для граждан с собственными яхтами и самолетами.</p>"},{_type:"post",id:525,uid:"utvYtOFN",title:'В настоящее время в Караганде завершается строительство ЖК "Шапагат"',slug:"v_nastoiashhee_vremia_v_karagande_zaversaetsia_stroitelstvo_zk_sapagat_525",short:'В этом году в шахтёрской столице завершится долгострой ЖК "Шапагат", и в эксплуатацию будут сданы третья и четвёртая блок-секции, каждая из которых состоит из 10 этажей.',likes_count:1,me_liked:!1,published_at:"2023-06-12T11:13:16.000000Z",image_preview:"https://kapster.kz/syc/kapster/22831/conversions/6486fdcc69466-preview.jpg",content:'<p>На строительной площадке ЖК "Шапагат" в настоящее время активно проводятся строительно-монтажные работы. Аким области Ермаганбет Булекпаев лично посетил строительные площадки жилых объектов во время рабочего объезда.</p><p><br>На строительной площадке недостроенных секций ЖК "Шапагат" в данный момент ведется кладка стен восьмого этажа. На стройке работает 65 рабочих, у которых есть все необходимое оборудование и материалы. Сотрудники технического надзора регулярно посещают объект, чтобы контролировать ход строительства. Параллельно с завершением строительных работ планируется благоустроить прилегающую территорию. Арендное жилье будет предоставлено 90 семьям. Ермаганбет Булекпаев поручил подрядчику соблюдать установленный график и обеспечивать высокое качество выполняемых работ. </p><p><br>Аким также осмотрел несколько других объектов. В настоящее время на улице Чапаева в Майкудуке возводится социальное кредитное жилье. Планируется сдать в эксплуатацию 108 квартир. Договор с предыдущим подрядчиком был расторгнут, и в этом году строительство девятиэтажного здания было взято новым подрядчиком.</p><p><br><em>– Сейчас идёт заливка второго этажа. Обычно делают два этажа в месяц. Окончание строительства запланировано на первый квартал 2024 года, – проинформировал руководитель Отдела строительства Караганды Мурат Камалиев.</em></p><p><br>На улице Белинского девятиэтажное здание уже находится на последней стадии строительства, завершено около 90% работ. В доме расположено 72 квартиры, которые будут предоставлены в аренду. В настоящее время строители активно проводят внутренние отделочные работы. В августе планируется начать благоустройство прилегающей территории. </p><p><br></p>'},{_type:"post",id:524,uid:"YKUAGVt-",title:"Причины снижения сделок купли-продажи на рынке жилья за последний месяц",slug:"priciny_snizeniia_sdelok_kupli_prodazi_na_rynke_zilia_za_poslednii_mesiac_524",short:"За последнее время количество сделок очень сократилось, наибольшее снижение заметили в Алматы и Астане. Первое кредитное бюро сообщает, что за май показатель упал на 18,6%.",likes_count:1,me_liked:!1,published_at:"2023-06-12T10:09:33.000000Z",image_preview:"https://kapster.kz/syc/kapster/22830/conversions/6486eedd3f234-preview.jpg",content:'<blockquote><p><em>"В мае казахстанцы совершили 25,6 тысячи сделок купли-продажи жилья. По сравнению с предыдущим месяцем это меньше на 18,6%, или 5,9 тысячи, следует из данных Бюро национальной статистики. Таким образом, после резкого скачка в апреле показатель откатился к среднемесячному уровню января-марта 2023 года – 24,2 тысячи", – говорится в сообщении.</em></p></blockquote><p><br>Также показатель выявил, что упадок не обошел и другие регионы Казахстана. </p><p>Статистика показала: </p><p><strong>Город Алматы – -1,3 тыс, до 4,3 тыс;</strong></p><p><strong>Город Астана – -1,1 тыс, до 3,7 тыс.</strong></p><p>В совокупности эти два города обеспечили около трети общего объема транзакций. </p><p>Также статистика показала наименьшие показатели в таких областях как:</p><p><strong>Мангистауская область – -566, до 1,6 тыс;</strong></p><p><strong>Карагандинская область – -479, до 2,4 тыс;</strong></p><p><strong>Жетысуская область – -211, когда максимальное значение достигло 581.</strong><br></p><blockquote><p><em>"Год назад на рынок жилья перестали поступать пенсионные накопления, что привело к его охлаждению. Но даже тогда показатель был значительно выше – 30,5 тысячи", – сообщили аналитики Первого кредитного бюро.</em></p></blockquote>'},{_type:"post",id:523,uid:"7Rig4di3",title:"875 млн тенге дополнительного финансирования на реализацию проектов получит Турксибский район Алматы",slug:"875_mln_tenge_dopolnitelnogo_finansirovaniia_na_realizaciiu_proektov_polucit_turksibskii_raion_almaty_523",short:"В рамках «Бюджета народного участия» Турксибский район получит 875 млн тенге на реализацию 56 проектов, а также еще 500 млн на ремонт магистральных улиц.",likes_count:0,me_liked:!1,published_at:"2023-06-09T11:51:09.000000Z",image_preview:"https://kapster.kz/syc/kapster/22817/conversions/6483122d9a5fe-preview.jpg",content:"<p>Деньги выделены на благоустройство мест общественного пользования, обустройство детских и спортивных площадок, а также освещение улиц. </p><p>Дополнительно, на ремонт 40 тыс м² магистральных улиц будет направлено 500 млн тенге. Построят дороги в микрорайоне Кайрат, протяженностью 2,7 км. Они будут введены до конца сентября этого года. В районе планируется провести ремонт дорог по 18 улицам протяженностью 24 км.</p><p>В районе будут запущены 8 новых автобусных маршрута и установлены 585 камер видеонаблюдения в местах массового скопления людей для повышения безопасности граждан. </p><p><br></p>"},{_type:"post",id:522,uid:"MuGKstx8",title:"Насколько вырастут цены на услуги ЖКХ в Алматы с 1 июля",slug:"naskolko_vyrastut_ceny_na_uslugi_zkx_v_almaty_s_1_iiulia_522",short:"С 1 июля тарифы ЖКХ в Алматы будут повышены. Монополии требуют повышения на 40-44%.",likes_count:0,me_liked:!1,published_at:"2023-06-09T11:37:15.000000Z",image_preview:"https://kapster.kz/syc/kapster/22816/conversions/64830eebf212a-preview.jpg",content:"<p>Но точная сумма повышения будет объявлена ​​в конце июня. Они не обещают резкого роста. Об этом сообщила начальник департамента комитета по регулированию естественных монополий Миннацэкономики РК по Алматы Асель Буденеева.</p><p><strong>Насколько вырастут процентные ставки?</strong></p><p>Точно неизвестно. Должно быть обсуждение. В скором времени мы узнаем больше информации, приблизительно 25 июня.</p><blockquote><p><em>— Сказать, на сколько повысят тарифы, я затрудняюсь. Заявки от монополистов поступили только вчера (8 июня. — Прим. ред.). Резкого, неправомерного скачка не будет. Более точную информацию о размере роста монополисты должны распространить за пять дней до введения, то есть 25 июня будет точнее известно, на сколько вырастут показатели, — сказала Асель Буденеева.</em></p></blockquote><p>В результате ожидается снижение количества аварий и отключений сетей, повышение качества коммунальных услуг, а в целом к ​​2029 году сеть будет обновлена ​​на 15%.</p><p><strong>Кто хочет поднять процентную ставку?</strong></p><p>Четыре монополии подали заявки на изменение тарифов на коммунальные услуги в г. Алматы, касающиеся следующих услуг:</p><ul><li><p><strong>Отопление, горячая вода — на 43 %.</strong></p></li><li><p><strong>Передача электроэнергии — 40 %.</strong></p></li><li><p><strong>Холодная вода, канализация — 44 %.</strong></p></li></ul><p>Проекты заявлений об изменении тарифов будут рассматриваться в соответствии с правилами тарифообразования. С этой целью будут проведены публичные слушания с депутатами маслихата, акимата, СМИ, общественниками, независимыми экспертами и потребителями.</p><p>Нуждающиеся жители получат жилищную помощь</p><p><strong>Износ сетей</strong></p><p><strong>В красной зоне по уровню износа сетей находятся:</strong></p><p><strong>— АО «Алатау Жарык Компаниясы — износ 92.67 %;</strong></p><p><strong>— ТОО «Алматытеплокоммунэнерго» — 67.9 %.</strong></p><p><strong>В жёлтой зоне:</strong></p><p><strong>— ТОО «Алматинские тепловые сети» — 59.2 %;</strong></p><p><strong>— АО «Алматинские электрические станции» — 61.5 %.</strong></p><p><strong>В зелёной — ГКП на ПХВ «Алматы су»: по водоснабжению — 56.4 %, канализации — 57.8 %.</strong></p>"},{_type:"post",id:521,uid:"2LevLm3M",title:"339 незаконных объектов торговли демонтируют к концу года в Алматы",slug:"339_nezakonnyx_obieektov_torgovli_demontiruiut_k_koncu_goda_v_almaty_521",short:"Нестационарные объекты торговли портят внешний вид города и нарушают благоустройство. Акимат города Алматы принимает соответствующие меры для борьбы с незаконными объектами.",likes_count:0,me_liked:!1,published_at:"2023-06-09T11:07:18.000000Z",image_preview:"https://kapster.kz/syc/kapster/22815/conversions/648307e64ee06-preview.jpg",content:"<p>Мониторинг города показал свыше 500 таких несанкционированных объектов на конец 2022 года. 101 из них уже демонтированы. Оставшиеся демонтируют до конца текущего года. Данные объекты действуют с нарушением земельного законодательства, санитарных, градостроительных и пожарных норм.<br>Данные меры принимаются в рамках поручения Акима города «Правила установки нестационарных объектов торговли на территории города Алматы и в пригородной зоне», где четко обусловлены правила установки коммерческих объектов.</p>"},{_type:"post",id:520,uid:"lWpPnaFQ",title:"В Алматы снесли гостиницу «Туркестан», что будет построено на ее месте?",slug:"v_almaty_snesli_gostinicu_turkestan_cto_budet_postroeno_na_ee_meste_520",short:"Представитель компании раскрыл причины сноса и рассказал, что будет построено на её месте.",likes_count:0,me_liked:!1,published_at:"2023-06-08T11:29:36.000000Z",image_preview:"https://kapster.kz/syc/kapster/22771/conversions/6481bba0d7528-preview.jpg",content:'<p>По словам представителя компании Upstate, Жанаса Абуталипа, данный гостиничный комплекс компания приобрела еще в январе этого года.       </p><blockquote><p><em>"«Туркестан» состоял в основном из зданий торгового павильона 1965 года и гостиницы 1979 года постройки. Экспертиза показала, что грунты основания и конструкция зданий в целом не отвечают современным сейсмическим требованиям. Кроме того, обветшалые и изношенные инженерные сети требовали огромного объема ремонтно-восстановительных работ. В конце марта мы получили разрешение на снос зданий и довели эту информацию до руководства районного акимата", – объяснил Жанас Абуталип. </em></p></blockquote><p>На месте снесенного здания будет построен современный торгово-гостиничный комплекс Turkistan. Фирма хотела, чтобы объект вписался в застроенную среду и стал своего рода достопримечательностью на улице Пушкина, часть которой столичные власти планируют сделать пешеходной.</p><blockquote><p><em>"Демонтаж ещё не закончили, но уже прорабатываем вопрос по получению разрешительной документации на мероприятия, связанные с новым строительством на месте", – сообщил представитель компании.</em></p></blockquote>'},{_type:"post",id:519,uid:"SI_F2Xt9",title:"Какие работы запланированы в Карагандинской области до конца года",slug:"kakie_raboty_zaplanirovany_v_karagandinskoi_oblasti_do_konca_goda_519",short:"Дороги восьми населённых пунктов отремонтируют в этом году в рамках проекта «Ауыл - Ел бесігі» в Нуринском районе. Дороги отремонтируют в городе Темиртау, помимо этого запланированы различные общественные места.",likes_count:0,me_liked:!1,published_at:"2023-06-08T11:25:02.000000Z",image_preview:"https://kapster.kz/syc/kapster/22770/conversions/6481ba8eabbb0-preview.jpg",content:"<p>В селе Изенды, где проживают 476 человек, реконструируют водопроводные сети и установят новые опреснители.</p><p>«Мы охватим ремонтом 53 километра. Ранее столько никогда за сезон не делали. Асфальтировать будем центральные улицы и внутриквартальные, а также дворовые территории. Параллельно идет ямочный ремонт плохих участков дорог. Положат асфальт там, где когда-то ликвидировали водопроводные аварии «Окжетпес» и «Аква-Трейдинг»», — сообщил руководитель отдела ЖКХ города Темиртау, пассажирского транспорта и автодорог Искандер Кабидулла.</p><p>В городе также отремонтируют почти 29 километров сетей теплоснабжения, однако демонтаж труб не допускают по причине отсутствия материалов. </p><p>В Темиртау установят 30 детских площадок и 10 футбольных кортов. Запланирована установка 12 мафов, тренажёров, беседок и скамеек, обсуждается ремонт спортивных объектов.  Подрядчиком выступает местная компания.</p><p>В Егинди будут охвачены 9 км дорог, это 4 улицы и 4 проулка. В Байтугане - 7,8 км. В селе Ахмет отремонтируют дороги 14 улиц протяженностью 14 км. В селе имени Карима Мынбаева асфальтирование уже завершается. Подрядные компании приступили к выравниванию дорожного покрытия и готовят улицы к асфальтированию.</p>"},{_type:"post",id:518,uid:"TfhY7VF1",title:"Завершились обсуждения первого проекта Строительного кодекса РК",slug:"zaversilis_obsuzdeniia_pervogo_proekta_stroitelnogo_kodeksa_rk_518",short:"Рабочая группа начала свою работу в начале мая, были проведены обсуждения во всех областных центрах.",likes_count:0,me_liked:!1,published_at:"2023-06-07T11:15:54.000000Z",image_preview:"https://kapster.kz/syc/kapster/22761/conversions/648066ea7127a-preview.jpg",content:"<p>Кодекс решит все актуальные проблемы в сферах градостроительства и архитектуры. Была введена информационная система E-Qurylys, которая позволяет в онлайн-режиме контролировать процесс строительства на всех этапах.</p><p>Проект вызвал большой интерес общественности, все предложения и замечания будут учтены при вводе кодекса. </p><p>В обсуждениях изменений участвовали представители Министерства индустрии и инфраструктурного развития, Комитета по делам строительства и ЖКХ, АО «Казахский научно-исследовательский и проектный институт строительства и архитектуры», а также местных исполнительных органов, представителей бизнеса и экспертов строительной отрасли.</p>"},{_type:"post",id:517,uid:"gfaGD9I7",title:"Дольщики в Актобе не могут въехать в свои квартиры",slug:"dolshhiki_v_aktobe_ne_mogut_vieexat_v_svoi_kvartiry_517",short:'Дольщики многоквартирного жилого комплекса "Казына Премиум" не могут въехать в свои квартиры с августа 2022 года. Большинство людей для приобретения жилья в этом комплексе продали свою уже имеющуюся жилплощадь. Теперь люди вынуждены жить в съемных квартирах.',likes_count:0,me_liked:!1,published_at:"2023-06-07T05:19:18.000000Z",image_preview:"https://kapster.kz/syc/kapster/22753/conversions/648013560abe7-preview.jpg",content:'<p>Участники долевого строительства до сих пор не могут получить ни жилья и даже документов на квартиру. Некоторые дольщики отметили, что внесли 100% оплату за жилье.</p><blockquote><p><em>"Никаких вразумительных ответов не получаем от них. Ни конкретных сроков, ни этапов работ – ничего, никакого ответа не дают. Обращались и письменно к ним, тоже полностью игнорируют наше обращение, обратились в прокуратуру также", – сообщает один из дольщиков Азамат Жолмуханбетов.</em></p></blockquote><p>Так же его слова подтвердила еще одна дольщица – Гаухар Тажигулова.  </p><blockquote><p><em>"В 2021 году в ноябре отдали свои средства, выкупили квартиры. Прошло два года, нет ничего у нас. А ведь мы заплатили немалые деньги: я, к примеру, отдала 15,5 млн тенге за двухкомнатную квартиру. Цена квадратного метра составила 220 тысяч тенге", – дополнила она.</em></p></blockquote><p>Расима Латыпова, председатель Гражданской ассоциации по защите прав потребителей, советует перед вложением денег в совместное строительство тщательно изучить информацию о застройщике. Она также советует не давать деньги наличными. Это связано с тем, что в случае судебного разбирательства оплата через банк является весомым доказательством.</p><blockquote><p><em>"Не надо заключать договоры предварительной купли-продажи – это, в первую очередь, предварительный договор участия в долевом строительстве, договор бронирования либо займа и другие сделки договора, не связанные с договором в долевом участии. Потому что эти договоры как таковые не являются документом, подтверждающим то, что вы купили эту квартиру", – предупредила юрист.</em></p></blockquote><p>Между тем, руководитель объекта уверен, что сдача объекта состоится в сентябре этого года. По его словам, 90% работ уже завершено, а задержка сдачи дома связана с ростом цен на строительные материалы.</p>'},{_type:"post",id:516,uid:"a5160UeR",title:"Оформить прописку теперь можно в мобильном приложении Kaspi.kz",slug:"oformit_propisku_teper_mozno_v_mobilnom_prilozenii_kaspikz_516",short:"Банк Kaspi.kz совместно с Комитетом миграционной службы МВД, Министерством юстиции, Министерством цифровизации, РГП ИПЦ и АО НИТ запустили новый сервис, который позволит оформить постоянную и временную прописку прямо в приложении Kaspi.kz.",likes_count:0,me_liked:!1,published_at:"2023-06-06T11:29:25.000000Z",image_preview:"https://kapster.kz/syc/kapster/22752/conversions/647f18d28219d-preview.jpg",content:"<p>Сервис позволяет прописывать других людей в собственной квартире и зарегистрироваться самому, а также выписывать людей, больше не живущих в доме. Кроме того можно отправить заявку, чтобы приписать себя у кого-то по ИИНу. </p><p>При оформлении прописки необходимо пройти фотоверификацию.</p><p>«Популярность услуг в мобильных приложениях БВУ растет ежегодно. К примеру, за прошлый год свыше 1 млн услуг были получены через внешние платформы. В целом вывод госуслуг в мобильные приложения банков второго уровня направлен на повышение удобства получения госуслуг и обеспечение их доступности. Совместная работа министерства, БВУ и заинтересованных госорганов будет продолжена в данном направлении», — отметил Асет Турысов, вице-министр МЦРИАП РК. </p>"}],d={style:{"font-family":"'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif"}},q=k({__name:"index",setup(m){const r=l();function _(){r.changedPosts(i)}return(v,p)=>(t(),e("div",d,[o(n),(t(!0),e(u,null,c(g(i),s=>(t(),e("div",{key:s.id,class:"col-12 container-fluid"},[o(a,{class:"mt-4",post:s,onClick:p[0]||(p[0]=b=>_())},null,8,["post"])]))),128))]))}});export{q as default};
